import React, {Component} from 'react';
import Question from './components/Question';
import axios from 'axios';
import './App.css'

class App extends Component {
  state = {
    data: {},
    isLoading: true,
//    dateCut: '', 
    dataKeys: [],
    dataValues: []
}


  async componentDidMount(){
      //insert the current date in the url so we only display 
    const {data} = await axios(`https://opentdb.com/api.php?amount=10`)
// go through all results (return objects)   
      const results = data.results
      console.log("RESULTS : " + results)
        // for each result go through all keys (return an array of keys)
      const keys = results.map((result, i) => {
        return Object.keys(result)
      })
      console.log(keys)
        // for each result go through all values (return an array of results)

        const values = results.map((result, i) => {
          return Object.values(result)
        })
        console.log(values)


        const QuestionResponse = results.map((result, i) => {
          return `${Object.keys(result)[3] + Object.values(result)[3]} ` 
        })

        console.log("RESPOOOOONSE" + QuestionResponse)

          this.setState({
            data, 
            isLoading: false,
            dataKeys: keys,
            dataValues: values,

  //          categories: 
  //          dateCut: data[data.length-1].data_inici.substr(0,10).split('-').join(',')

          })

          console.log("DATA KEYS : " + Object.keys(data.results[0]))
          console.log(data.results[0].category)
          console.log(this.state.results)
          // const datasArray = data.results.map((result, i) => {
          //   return ({...result})
          // })

        }





  render(){
    return (
      <div className="">
          {this.state.isLoading
          ? "IT DIDN'T LOAD"
          : "IT HAS LOADED"
          }
      {/* <Question results={this.state.data.results}/> */}
        {/* "THE CATEGORY : " + this.state.data.results} */}
        {this.state.dataKeys.map((key, y) => {
          return "MY FIELD : " + key[3]
        }
        )}
        {this.state.dataValues}


      </div>
    );
  }
}
export default App;
