import React, {Component} from 'react';
import Question from './components/Question';
import axios from 'axios';
import './App.css'

class App extends Component {
  state = {
    data: {},
    isLoading: true,
//    dateCut: '', 
    dataApi:[],
    results: [],
    firstTestCategory: ""
}


  async componentDidMount(){
      //insert the current date in the url so we only display 
    const {data} = await axios(`https://opentdb.com/api.php?amount=10`)

          this.setState({
            data, 
            isLoading: false,
            firstTestCategory: data.results[0].category,
            results: data.results

  //          categories: 
  //          dateCut: data[data.length-1].data_inici.substr(0,10).split('-').join(',')

          })
          const datasArray = data.results.map((result, i) => {
            return ({...result})
          })

        }



  render(){
    return (
      <div className="">
          {this.state.isLoading
          ? "IT DIDN'T LOAD"
          : "IT HAS LOADED"
          }
          {this.datasArray}
      {/* <Question results={this.state.data.results}/> */}
        {/* "THE CATEGORY : " + this.state.data.results} */}

      </div>
    );
  }
  }
export default App;
