import React, {Component} from 'react';
import Question from './components/Question';
import axios from 'axios';
import './App.css'

class App extends Component {
  state = {
    data: {},
    isLoading: true,
//    dateCut: '', 
    dataKeys: [],
    dataValues: [],
    questionResponseArray: [],
    number: 0,
    questions: [],
    falseResponses: [],
    trueResponses: []
}


  async componentDidMount(){
      //insert the current date in the url so we only display 
    const {data} = await axios(`https://opentdb.com/api.php?amount=10`)
// go through all results (return objects)   
      const results = data.results

      const questions = results.map((result: [], i) => {
        return Object.values(result)[3]
      })

      const falseResponses = results.map((result, i) => {
        return Object.values(result)[5]
      })

      const trueResponse = results.map((result, i) => {
        return Object.values(result)[4]
      })




      // console.log(keys)
      //   // for each result go through all values (return an array of results)

      //   const values = results.map((result, i) => {
      //     return Object.values(result)[3]
      //   })

      //   const questionResponse = results.map((result) => {
      //     return `${Object.values(result)[3]  +  "    " + Object.values(result)[4]}`
      //   })

        const setNumber = () => {
          return this.state.number + 1
        }

          this.setState({
            data, 
            isLoading: false,
            number: setNumber(),
            dataKeys: keys,
            dataValues: values,
            questionResponseArray: questionResponse,
            questions,
            falseResponses,
            trueResponses

  //          categories: 
  //          dateCut: data[data.length-1].data_inici.substr(0,10).split('-').join(',')

          })

          console.log("HELOOOOOOOOOOO" + this.state.questionResponseArray[0])
