          //   return ({...result})
          // })

        }





  render(){
    return (
      <div className="">
          {this.state.isLoading
          ? "IT DIDN'T LOAD"
          : "IT HAS LOADED"
          }
      {/* <Question results={this.state.data.results}/> */}
        {/* "THE CATEGORY : " + this.state.data.results} */}

          <h1>{this.state.questionResponseArray[this.state.number]}</h1>

        {this.state.questionResponse}


      </div>
    );
  }
}
export default App;
