import React from 'react';

 export default function Question(props) {
//     const categories = props.results.map((x, i) =>
//         x[i]
//     )
const direction = () => {
    const flexDirection = ["row", "rowreverse"];
    return flexDirection[Math.floor(Math.random() * 2)];
 }

    return (
        <div id="gameContainer">
        <div id="counter">{props.counter}</div>
        <div id="question">{props.questionsArray[props.number]}</div>
        <div id="responsesContainer" class={this.direction}>
          <div onClick={props.noValidation}>{props.fakeAnswersArray[props.number]}</div>
          <div onClick={props.answerValidation}>{props.trueAnswersArray[props.number]}</div>
        </div>
      </div>
    )
}
