        }







  render(){
    return (
      <div className="">
          {this.state.isLoading
          ? "IT DIDN'T LOAD"
          : "IT HAS LOADED"
          }
      {/* <Question results={this.state.data.results}/> */}
        {/* "THE CATEGORY : " + this.state.data.results} */}
          <div>COUNTER : {this.state.counter}</div>
          <h1>{this.state.questionsArray[this.state.number]}</h1>
          <h1>{this.state.fakeAnswersArray[this.state.number]}</h1>
          <div onClick={this.answerValidation}>{this.state.trueAnswersArray[this.state.number]}</div>



        {this.state.questions}



      </div>
    );
  }
}
export default App;
